<script>
    $(function() {
        var $topBar = $('#top-bar');

        $('a[href]').on('click', function() {
            var href = $(this).attr('href');
            var $section = $(href);
            if($section.length)
            {
                $('html, body').animate({
                    scrollTop: $section.offset().top - $topBar.height()
                }, 2000);
            }
            else
            {
                window.location = href;
            }
        });
    });
</script>